<template>
    <div class="box">
      <span>上传店铺头像</span>
      <div class="avatar-uploader">
<input class="input" type="file" @change="prevewTimg($event)">
  <div class="div">
    <img :src="imgContent" class="img">
  </div>
  
  <i class="el-icon-plus" v-show="show"></i>
</div>
    </div>
</template>
<script>
export default {
 data() {
      return {
        show:true,
       imgContent:"data:image/jpeg;base64,"
      };
    },
   methods:{
    prevewTimg(event){
      var input=event.currentTarget
      var reader=new FileReader();
      reader.readAsDataURL(input.files[0])
      reader.onload=()=>{
        this.show=false
        this.imgContent=reader.result
        
        // window.console.log(reader.result)
      }
    }
   }
}
</script>
